import React, { useState, useEffect } from "react";
import "./styles/search.css";

function SearchVerbs() {
    const [searchTerm, setSearchTerm] = useState("");
    const [verbs, setVerbs] = useState([]);
    const [result, setResult] = useState(null);

    useEffect(() => {
        // Cargar el archivo JSON con los verbos
        fetch("/verbs.json")
            .then(response => response.json())
            .then(data => setVerbs(data.data)) // Ajustado según la estructura del JSON
            .catch(error => console.error("Error loading verbs:", error));
    }, []);

    const handleSearch = (event) => {
        event.preventDefault();

        const verbFound = verbs.find(verb => 
            verb.simple_form.toLowerCase() === searchTerm.toLowerCase() ||
            verb.meaning.toLowerCase().includes(searchTerm.toLowerCase())
        );

        if (verbFound) {
            setResult(verbFound);
        } else {
            alert("Verbo no encontrado. Verifique la ortografía e inténtelo de nuevo.");
            setResult(null);
        }
    };

    return (
        <div>
            <form className="form-search" onSubmit={handleSearch}>
                <input
                    className="search-input"
                    type="search"
                    placeholder="Buscar verbo..."
                    maxLength={20}
                    required
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
                <button type="submit">Consultar</button>
            </form>
            {result && (
                <div className="results">
                    <ul>
                        <li><strong>Infinitivo:</strong> {result.simple_form}</li>
                        <li><strong>Tercera Persona:</strong> {result.third_person}</li>
                        <li><strong>Pasado Simple:</strong> {result.simple_past}</li>
                        <li><strong>Pasado Participio:</strong> {result.past_participle}</li>
                        <li><strong>Gerundio:</strong> {result.gerund}</li>
                        <li><strong>Traducción:</strong> {result.meaning}</li>
                    </ul>
                </div>
            )}
        </div>
    );
}

export default SearchVerbs;
